import React, { useState } from "react";

const studentNames = [
    "Ayush Bhatt",
    "Lovepreet Singh",
    "Parth Sinha",
    "Utkarsh Gautam",
    "Md.Saif",
    "Yash Baberwal",
    "Shivanand Rai",
    "Shubham Kumar",
    "Harshit Singh",
    "Vansh Malik",
    "Anuj",
    "Tushar",
    "Akshan Dhyani",
    "Manish Bisht",
    "Abhay Singh",
    "Kundan Kumar",
    "Ananya Mishra",
    "Praneet Sahay",
    "Jasdev Singh",
    "Aryan Tandon",
    "Shivansh Rajput",
    "Abhishek Rawat",
    "Sarvesh Mathur",
    "Ritesh Dixit"
  ];
  

const CertificateGallery = () => {
  const [selectedCertificate, setSelectedCertificate] = useState(null);

  const handleDownload = (name) => {
    const imageUrl = `/certificates/${name}.png`;
    const link = document.createElement("a");
    link.href = imageUrl;
    link.download = `${name}.png`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <div className="gallery-container">
      <div className="certificate-grid">
        {studentNames.map((name, index) => (
          <div key={index} className="certificate-card" onClick={() => setSelectedCertificate(name)}>
            <img src={`/certificates/${name}.png`} alt={`${name}'s Certificate`} className="certificate-img" />
            <p>{name}</p>
          </div>
        ))}
      </div>

      {selectedCertificate && (
        <div className="preview-modal" onClick={() => setSelectedCertificate(null)}>
          <div className="preview-content" onClick={(e) => e.stopPropagation()}>
            <h2>{selectedCertificate}'s Certificate</h2>
            <img src={`/certificates/${selectedCertificate}.png`} alt={`${selectedCertificate} Certificate`} className="preview-img" />
            <button onClick={() => handleDownload(selectedCertificate)}>Download</button>
            <button onClick={() => setSelectedCertificate(null)}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default CertificateGallery;
